class Solution:



    def longestStrChain(self, words) -> int:
        tree_dict = {}
        tree_dict[""] = 0
        words.sort(key=lambda x:len(x))
        max_value = 0
        for each in words:
            tree_dict[each] = 1
            for i in range(len(each)):
                each_sub = each[:i] + each[i+1:]
                if each_sub in tree_dict:
                    # print(each_sub)
                    tree_dict[each] = max(tree_dict[each], tree_dict[each_sub] + 1)

            max_value = max(max_value, tree_dict[each])
        return max_value

