class Solution:
    res = float("inf")
    def minFlipsMonoIncr(self, S):
        import collections
        """
        :type S: str
        :rtype: int
        """
        def DFS(index, count, list_check, prev=None):
            if count >= self.res:
                return
            else:
                if len(list_check) == len(S):
                    if check(list_check):
                        self.res = count
                    return

                else:
                    if not prev:
                        if S[index] == '0':
                            DFS(index+1, count+1, list_check+'1', '0')
                            DFS(index+1, count, list_check+'0', '0')
                        else:
                            DFS(index+1, count, list_check + '1', '1')
                            DFS(index+1, count+1, list_check + '0', '0')
                        return
                    else:
                        if prev == '1':
                            if S[index] == '0':
                                DFS(index+1, count+1, list_check+'1', '1')
                            else:
                                DFS(index + 1, count, list_check + '1', '1')
                            return
                        else:
                            if S[index] == '0':
                                DFS(index+1, count+1, list_check+'1', '1')
                                DFS(index + 1, count, list_check + '0', '0')
                            else:
                                # == 1
                                DFS(index + 1, count, list_check + '1', '1')
                                DFS(index + 1, count+1, list_check + '0', '0')
                            return


        def check(list_check):
            if len(set(list_check)) == 1:
                return True
            if list_check[0] == '1' or list_check[-1] == '0':
                return False

            start = 0
            end = len(list_check)-1
            while(list_check[start] == '0'):
                start += 1
            while(list_check[end] == '1'):
                end -= 1
            return end == start - 1


        if not S:
            return 0

        dict = collections.Counter(S)
        self.res = min(dict['0'], dict['1'])
        S = list(S)
        if len(set(S)) == 1:
            return 0
        DFS(0, 0, "")
        return self.res

    def minFlipsMonoIncr2(self, S):
        if not S:
            return 0

        if len(set(S)) == 1:
            return 0


        import collections

        total = collections.Counter(S)
        total_zero = total['0']
        total_one = total['1']
        res = min(total_one, total_zero)
        prev_zero = 0
        for i in range(len(S)):
            if res > i - prev_zero + total_zero - prev_zero:
                res = i - prev_zero + total_zero - prev_zero
            if S[i] == '0':
                prev_zero += 1
        return res





S = ""

s = Solution()
print(s.minFlipsMonoIncr2(S))